mapper.select:
select id, name, order_num
from lw_order_fundsupport
where id = #{id}

select id, name, status, order_num, create_by, create_time, update_by, update_time from lw_order_fundsupport where status=1
and name like concat('%', #{params.name}, '%')
order by order_num desc limit #{index}, #{pageSize}

mapper.update:
update lw_order_fundsupport
set name=#{name},
    order_num=#{orderNum},
    update_by=#{updateBy},
    update_time=#{updateTime}
where id = #{id}

update lw_order_fundsupport
set status = 1
where id = #{id}

update lw_order_fundsupport
set status = 0
where id = #{id}
